import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './styles.css';
import logoImage from '../logo.jpg';

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.startsWith(name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

const STATUS_OPTIONS = [
  ['PENDING', 'Pending'],
  ['APPROVED', 'Approved'],
  ['REJECTED', 'Rejected']
];

const MASTER_GROUPS = [
  ['TRAVEL', 'Travel'],
  ['OFFICE_SUPPLIES', 'Office Supplies'],
  ['UTILITIES', 'Utilities'],
];

const CURRENCIES = [
  ['USD', 'USD'],
  ['EUR', 'EUR'],
  ['GBP', 'GBP'],
  ['INR', 'INR'],
];

const SUBGROUPS = {
  'TRAVEL': [
    ['TICKET', 'Ticket Expense'],
    ['FOOD', 'Food Expense'],
    ['HOSPITALITY', 'Hospitality Expense'],
  ],
  'OFFICE_SUPPLIES': [
    ['EQUIPMENT', 'Equipment'],
    ['STATIONERY', 'Stationery'],
  ],
  'UTILITIES': [
    ['INTERNET', 'Internet'],
    ['ELECTRICITY', 'Electricity'],
  ],
};

const DetailsScreen = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [expenses, setExpenses] = useState([]);
  const [expenseTitles, setExpenseTitles] = useState([]);
  const [selectedTitleId, setSelectedTitleId] = useState(location.state?.selectedTitleId || null);
  const [pendingEdits, setPendingEdits] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [titleStatus, setTitleStatus] = useState({
    status: '',
    comments: ''
  });
  const [searchQuery, setSearchQuery] = useState('');
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [editingExpense, setEditingExpense] = useState(null);
  const [showCopyModal, setShowCopyModal] = useState(false);
  const [copySourceTitle, setCopySourceTitle] = useState(null);
  const [newTitleName, setNewTitleName] = useState('');
  const [copyMode, setCopyMode] = useState('all'); // 'all' or 'select'
  const [selectedForms, setSelectedForms] = useState([]);

  useEffect(() => {
    const adminStatus = localStorage.getItem('isAdmin') === 'true';
    setIsAdmin(adminStatus);
    fetchData(selectedTitleId);
  }, [selectedTitleId]);

  const fetchData = async (titleId = null) => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      };

      const expensesUrl = titleId ? `/api/expense-forms/?title_id=${titleId}` : '/api/expense-forms/';
      const expensesResponse = await axios.get(expensesUrl, config);
      setExpenses(expensesResponse.data);

      const titlesResponse = await axios.get('/api/expense-titles/', config);
      setExpenseTitles(titlesResponse.data);

      if (titleId && expensesResponse.data.length > 0) {
        const firstExpense = expensesResponse.data[0];
        setTitleStatus({
          status: firstExpense.status || '',
          comments: firstExpense.comments || ''
        });
      } else {
        setTitleStatus({
          status: '',
          comments: ''
        });
      }

      setLoading(false);
    } catch (error) {
      setError('Failed to fetch data');
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    localStorage.removeItem('username');
    navigate('/login');
  };

  const handleTitleSelect = (titleId) => {
    setSelectedTitleId(titleId);
    setPendingEdits({});
    fetchData(titleId);
  };

  const selectedTitle = expenseTitles.find(title => title.id === selectedTitleId);
  const filteredExpenses = selectedTitleId
    ? expenses.filter(expense => expense.expense_title && expense.expense_title.id === selectedTitleId)
    : expenses;

  const filteredTitles = expenseTitles.filter(title =>
    title.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
    setShowSuggestions(true);
  };

  const handleSuggestionClick = (titleId) => {
    setSelectedTitleId(titleId);
    setSearchQuery('');
    setShowSuggestions(false);
  };

  const handleSearchBlur = () => {
    setTimeout(() => {
      setShowSuggestions(false);
    }, 200);
  };

  const handleAdminUpdate = async (expenseId, updatedFields) => {
    const csrftoken = getCookie('csrftoken');
    const token = localStorage.getItem('token');
    try {
      const response = await axios.patch(`/api/expense-forms/${expenseId}/update_status/`, updatedFields, {
        headers: {
          'X-CSRFToken': csrftoken,
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });
      setExpenses(prevExpenses => 
        prevExpenses.map(expense => 
          expense.id === expenseId 
            ? { ...expense, ...updatedFields }
            : expense
        )
      );
      setPendingEdits(prev => {
        const newEdits = { ...prev };
        delete newEdits[expenseId];
        return newEdits;
      });
    } catch (error) {
      console.error('Error updating expense:', error.response?.data || error.message);
      setError('Failed to update expense. Please try again.');
    }
  };

  const handleTitleStatusUpdate = async () => {
    if (!selectedTitleId || !titleStatus.status) return;

    const csrftoken = getCookie('csrftoken');
    const token = localStorage.getItem('token');

    try {
      const formsToUpdate = expenses.filter(expense => 
        expense.expense_title?.id === selectedTitleId
      );

      setExpenses(prevExpenses => 
        prevExpenses.map(expense => 
          expense.expense_title?.id === selectedTitleId
            ? { 
                ...expense, 
                status: titleStatus.status,
                comments: titleStatus.comments
              }
            : expense
        )
      );

      const promises = formsToUpdate.map(expense => 
        axios.patch(`/api/expense-forms/${expense.id}/update_status/`, {
          status: titleStatus.status,
          comments: titleStatus.comments
        }, {
          headers: {
            'X-CSRFToken': csrftoken,
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
        })
      );

      await Promise.all(promises);

      const expensesUrl = `/api/expense-forms/?title_id=${selectedTitleId}`;
      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      };

      const response = await axios.get(expensesUrl, config);
      setExpenses(response.data);

      setError('');
    } catch (error) {
      console.error('Error updating expenses:', error.response?.data || error.message);
      setError('Failed to update expenses. Please try again.');
      fetchData(selectedTitleId);
    }
  };

  const handleEditExpense = (expense) => {
    setEditingExpense(expense.id);
    setPendingEdits({
      [expense.id]: {
        amount: expense.amount,
        date: expense.date,
        master_group: expense.master_group,
        subgroup: expense.subgroup
      }
    });
  };

  const handleMasterGroupChange = (expenseId, value) => {
    setPendingEdits(prev => ({
      ...prev,
      [expenseId]: {
        ...prev[expenseId],
        master_group: value,
        subgroup: ''
      }
    }));
  };

  const handleCancelEdit = () => {
    setEditingExpense(null);
    setPendingEdits({});
  };

  const handleSaveEdit = async (expenseId) => {
    const csrftoken = getCookie('csrftoken');
    const token = localStorage.getItem('token');

    try {
      const response = await axios.patch(`/api/expense-forms/${expenseId}/`, 
        pendingEdits[expenseId],
        {
          headers: {
            'X-CSRFToken': csrftoken,
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
        }
      );

      setExpenses(prevExpenses => 
        prevExpenses.map(expense => 
          expense.id === expenseId 
            ? { ...expense, ...response.data }
            : expense
        )
      );

      setEditingExpense(null);
      setPendingEdits({});
      setError('');
    } catch (error) {
      console.error('Error updating expense:', error.response?.data || error.message);
      setError('Failed to update expense. Please try again.');
    }
  };

  const handleCopyTitle = async () => {
    if (!copySourceTitle || !newTitleName) return;
    if (copyMode === 'select' && selectedForms.length === 0) return;

    try {
      const token = localStorage.getItem('token');
      const csrftoken = getCookie('csrftoken');

      // First create the new title
      const titleResponse = await axios.post('/api/expense-titles/', {
        title: newTitleName
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'X-CSRFToken': csrftoken,
          'Content-Type': 'application/json'
        }
      });

      const newTitleId = titleResponse.data.id;

      // Get forms to copy based on mode
      const sourceForms = expenses.filter(expense => {
        if (copyMode === 'all') {
          return expense.expense_title?.id === copySourceTitle;
        } else {
          return selectedForms.includes(expense.id);
        }
      });

      // Copy selected forms to the new title
      const copyPromises = sourceForms.map(form => 
        axios.post('/api/expense-forms/', {
          ...form,
          expense_title: newTitleId,
          id: undefined // Remove the ID to create a new form
        }, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'X-CSRFToken': csrftoken,
            'Content-Type': 'application/json'
          }
        })
      );

      await Promise.all(copyPromises);

      // Refresh the data
      await fetchData();
      setShowCopyModal(false);
      setNewTitleName('');
      setCopySourceTitle(null);
      setCopyMode('all');
      setSelectedForms([]);

    } catch (error) {
      console.error('Error copying title:', error);
      setError('Failed to copy expense title. Please try again.');
    }
  };

  if (loading) return <div className="container">Loading...</div>;
  if (error) return <div className="container error-message">{error}</div>;

  return (
    <div className="container">
      <div className="app-title-bar">
        <div className="logo-container">
          <img src={logoImage} alt="Logo" className="logo-img" />
          <span className="title">Expense Details</span>
        </div>
        <div className="search-wrapper">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search expense titles..."
              value={searchQuery}
              onChange={handleSearch}
              onFocus={() => setShowSuggestions(true)}
              onBlur={handleSearchBlur}
              className="search-input"
            />
            {showSuggestions && searchQuery && (
              <div className="search-suggestions">
                {filteredTitles.length > 0 ? (
                  filteredTitles.map((title) => (
                    <div
                      key={title.id}
                      className="suggestion-item"
                      onClick={() => handleSuggestionClick(title.id)}
                    >
                      {title.title}
                    </div>
                  ))
                ) : (
                  <div className="no-results">No matching titles found</div>
                )}
              </div>
            )}
          </div>
          <button onClick={handleLogout} className="btn">Logout</button>
        </div>
      </div>

      {/* New Expense Title Section */}
      <div className="container new-title-section">
        <div className="card">
          <h3>New Expense Title</h3>
          <form>
            <div className="form-group">
              <label htmlFor="expenseTitle">New Expense Title</label>
              <input
                type="text"
                id="expenseTitle"
                required
                placeholder="Enter new expense title"
                value={newTitleName}
                onChange={(e) => setNewTitleName(e.target.value)}
              />
            </div>
            <div className="form-group">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => setShowCopyModal(true)}
                style={{ marginRight: '10px' }}
              >
                Copy Expense
              </button>
              <button
                type="submit"
                className="btn"
              >
                Add Titl
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Copy Modal */}
      {showCopyModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Copy Expense Title</h3>
            <div className="form-group">
              <label>Source Title</label>
              <select
                value={copySourceTitle || ''}
                onChange={(e) => setCopySourceTitle(e.target.value)}
                className="modal-select"
              >
                <option value="">Select a title to copy</option>
                {expenseTitles.map(title => (
                  <option key={title.id} value={title.id}>{title.title}</option>
                ))}
              </select>
            </div>
            <div className="form-group">
              <label>New Title Name</label>
              <input
                type="text"
                value={newTitleName}
                onChange={(e) => setNewTitleName(e.target.value)}
                placeholder="Enter new title name"
                className="modal-input"
              />
            </div>
            <div className="form-group">
              <label>Copy Mode</label>
              <div className="radio-group">
                <label>
                  <input
                    type="radio"
                    value="all"
                    checked={copyMode === 'all'}
                    onChange={(e) => setCopyMode(e.target.value)}
                  />
                  Copy All Forms
                </label>
                <label>
                  <input
                    type="radio"
                    value="select"
                    checked={copyMode === 'select'}
                    onChange={(e) => setCopyMode(e.target.value)}
                  />
                  Select Forms
                </label>
              </div>
            </div>
            {copyMode === 'select' && copySourceTitle && (
              <div className="form-selection">
                <h4>Select Forms to Copy</h4>
                <div className="form-list">
                  {expenses
                    .filter(expense => expense.expense_title?.id === copySourceTitle)
                    .map(expense => (
                      <div key={expense.id} className="form-item">
                        <label>
                          <input
                            type="checkbox"
                            checked={selectedForms.includes(expense.id)}
                            onChange={(e) => {
                              if (e.target.checked) {
                                setSelectedForms([...selectedForms, expense.id]);
                              } else {
                                setSelectedForms(selectedForms.filter(id => id !== expense.id));
                              }
                            }}
                          />
                          <span>{expense.master_group} - {expense.subgroup} ({expense.amount} {expense.currency})</span>
                        </label>
                      </div>
                    ))}
                </div>
              </div>
            )}
            <div className="modal-actions">
              <button
                className="btn"
                onClick={handleCopyTitle}
                disabled={!copySourceTitle || !newTitleName || (copyMode === 'select' && selectedForms.length === 0)}
              >
                Copy
              </button>
              <button
                className="btn btn-secondary"
                onClick={() => {
                  setShowCopyModal(false);
                  setNewTitleName('');
                  setCopySourceTitle(null);
                  setCopyMode('all');
                  setSelectedForms([]);
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="expense-details-container">
        <div className="expense-titles-sidebar">
          <div className="card">
            <div className="sidebar-header">
              <h3>Expense Titles</h3>
              <div className="sidebar-actions">
                <button 
                  onClick={() => setShowCopyModal(true)} 
                  className="btn btn-secondary"
                  style={{ marginRight: '8px' }}
                >
                  Copy Expense
                </button>
                <button className="btn">Add Title</button>
              </div>
            </div>
            <div className="expense-titles-list">
              {expenseTitles.map((title) => (
                <div
                  key={title.id}
                  className={`expense-item ${selectedTitleId === title.id ? 'selected' : ''}`}
                  onClick={() => handleTitleSelect(title.id)}
                  style={{ cursor: 'pointer' }}
                >
                  <p>{title.title}</p>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div className="expense-forms-main">
          <div className="expense-forms-container">
            <div className="card">
              <h3>Expense Forms</h3>
              {selectedTitle && <h2>{selectedTitle.title}</h2>}

              {filteredExpenses.map((expense) => (
                <div key={expense.id} className="expense-item">
                  <div className="form-row">
                    <div className="form-group">
                      <div>Master Group</div>
                      {editingExpense === expense.id ? (
                        <select
                          value={pendingEdits[expense.id]?.master_group || expense.master_group}
                          onChange={(e) => handleMasterGroupChange(expense.id, e.target.value)}
                        >
                          {MASTER_GROUPS.map(([value, label]) => (
                            <option key={value} value={value}>{label}</option>
                          ))}
                        </select>
                      ) : (
                        <div>{MASTER_GROUPS.find(([value]) => value === expense.master_group)?.[1] || expense.master_group}</div>
                      )}
                    </div>

                    <div className="form-group">
                      <div>Subgroup</div>
                      {editingExpense === expense.id ? (
                        <select
                          value={pendingEdits[expense.id]?.subgroup || expense.subgroup}
                          onChange={(e) => setPendingEdits(prev => ({
                            ...prev,
                            [expense.id]: {
                              ...prev[expense.id],
                              subgroup: e.target.value
                            }
                          }))}
                        >
                          {SUBGROUPS[pendingEdits[expense.id]?.master_group || expense.master_group]?.map(([value, label]) => (
                            <option key={value} value={value}>{label}</option>
                          ))}
                        </select>
                      ) : (
                        <div>{SUBGROUPS[expense.master_group]?.find(([value]) => value === expense.subgroup)?.[1] || expense.subgroup}</div>
                      )}
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <div>CURR</div>
                      {editingExpense === expense.id ? (
                        <select
                          value={pendingEdits[expense.id]?.currency || expense.currency}
                          onChange={(e) => setPendingEdits(prev => ({
                            ...prev,
                            [expense.id]: {
                              ...prev[expense.id],
                              currency: e.target.value
                            }
                          }))}
                        >
                          {CURRENCIES.map(([value, label]) => (
                            <option key={value} value={value}>{label}</option>
                          ))}
                        </select>
                      ) : (
                        <div>{expense.currency}</div>
                      )}
                    </div>

                    <div className="form-group">
                      <div>Amount</div>
                      {editingExpense === expense.id ? (
                        <input
                          type="number"
                          value={pendingEdits[expense.id]?.amount || expense.amount}
                          onChange={(e) => setPendingEdits(prev => ({
                            ...prev,
                            [expense.id]: {
                              ...prev[expense.id],
                              amount: e.target.value
                            }
                          }))}
                        />
                      ) : (
                        <div>{expense.amount}</div>
                      )}
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <div>Date</div>
                      {editingExpense === expense.id ? (
                        <input
                          type="date"
                          value={pendingEdits[expense.id]?.date || expense.date}
                          onChange={(e) => setPendingEdits(prev => ({
                            ...prev,
                            [expense.id]: {
                              ...prev[expense.id],
                              date: e.target.value
                            }
                          }))}
                        />
                      ) : (
                        <div>{expense.date}</div>
                      )}
                    </div>
                  </div>

                  {expense.attachment && (
                    <div className="form-group">
                      <div>Attachment</div>
                      <a
                        href={expense.attachment}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="attachment-link"
                      >
                        View Attachment
                      </a>
                    </div>
                  )}

                  <div className="current-status-section">
                    <h4>Status Information</h4>
                    <div className="form-row">
                      <div className="form-group">
                        <div>Status</div>
                        <div className="status-display">{expense.status || 'Not Set'}</div>
                      </div>
                      <div className="form-group">
                        <div>Comments</div>
                        <div className="comments-display">{expense.comments || 'No comments'}</div>
                      </div>
                    </div>
                  </div>

                  <div className="form-actions">
                    {editingExpense === expense.id ? (
                      <>
                        <button
                          className="btn"
                          onClick={() => handleSaveEdit(expense.id)}
                        >
                          Save Changes
                        </button>
                        <button
                          className="btn btn-secondary"
                          onClick={handleCancelEdit}
                        >
                          Cancel
                        </button>
                      </>
                    ) : (
                      <button
                        className="btn"
                        onClick={() => handleEditExpense(expense)}
                      >
                        Edit Form
                      </button>
                    )}
                  </div>
                </div>
              ))}

              {isAdmin && selectedTitleId && (
                <div className="title-status-section">
                  <h4>Status and Comments</h4>
                  <div className="form-row">
                    <div className="form-group">
                      <div>Status</div>
                      <select
                        value={titleStatus.status}
                        onChange={(e) => setTitleStatus(prev => ({
                          ...prev,
                          status: e.target.value
                        }))}
                      >
                        <option value="">Select Status</option>
                        {STATUS_OPTIONS.map(([value, label]) => (
                          <option key={value} value={value}>{label}</option>
                        ))}
                      </select>
                    </div>
                    <div className="form-group">
                      <div>Comments</div>
                      <textarea
                        value={titleStatus.comments}
                        onChange={(e) => setTitleStatus(prev => ({
                          ...prev,
                          comments: e.target.value
                        }))}
                        placeholder="Enter comments for all forms"
                      />
                    </div>
                    <button
                      className="btn"
                      onClick={handleTitleStatusUpdate}
                      disabled={!titleStatus.status}
                    >
                      Update Status
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailsScreen;
