import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './styles.css';
import logoImage from '../logo.jpg';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.startsWith(name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

const STATUS_OPTIONS = [
    ['PENDING', 'Pending'],
    ['APPROVED', 'Approved'],
    ['REJECTED', 'Rejected']
];

const MASTER_GROUPS = [
    ['TRAVEL', 'Travel'],
    ['OFFICE_SUPPLIES', 'Office Supplies'],
    ['UTILITIES', 'Utilities'],
];

const CURRENCIES = [
    ['USD', 'USD'],
    ['EUR', 'EUR'],
    ['GBP', 'GBP'],
    ['INR', 'INR'],
];

const SUBGROUPS = {
    'TRAVEL': [
        ['TICKET', 'Ticket Expense'],
        ['FOOD', 'Food Expense'],
        ['HOSPITALITY', 'Hospitality Expense'],
    ],
    'OFFICE_SUPPLIES': [
        ['EQUIPMENT', 'Equipment'],
        ['STATIONERY', 'Stationery'],
    ],
    'UTILITIES': [
        ['INTERNET', 'Internet'],
        ['ELECTRICITY', 'Electricity'],
    ],
};

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const DetailsScreen = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [expenses, setExpenses] = useState([]);
    const [expenseTitles, setExpenseTitles] = useState([]);
    const [selectedTitleId, setSelectedTitleId] = useState(location.state?.selectedTitleId || null);
    const [pendingEdits, setPendingEdits] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [isAdmin, setIsAdmin] = useState(false);
    const [titleStatus, setTitleStatus] = useState({
        status: '',
        comments: ''
    });
    const [searchQuery, setSearchQuery] = useState('');
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [editingExpense, setEditingExpense] = useState(null);
    const [newTitle, setNewTitle] = useState('');
    const [showCopyOptions, setShowCopyOptions] = useState(false);
    const [copySourceTitleId, setCopySourceTitleId] = useState(null);
    const [copyForms, setCopyForms] = useState(true);
    const [activeTab, setActiveTab] = useState('details');
    const [stats, setStats] = useState({
        totalExpenses: 0,
        pendingCount: 0,
        approvedCount: 0,
        rejectedCount: 0
    });
    const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
    const [expenseToDelete, setExpenseToDelete] = useState(null);
    const [showAttachmentModal, setShowAttachmentModal] = useState(false);
    const [currentAttachment, setCurrentAttachment] = useState('');

    useEffect(() => {
        const isUserAdmin = localStorage.getItem('username') === 'admin';
        setIsAdmin(isUserAdmin);
        localStorage.setItem('isAdmin', isUserAdmin);
        fetchData(selectedTitleId);
    }, [selectedTitleId]);

    const fetchData = async (titleId = null) => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const config = {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            };

            const expensesUrl = titleId ? `/api/expense-forms/?title_id=${titleId}` : '/api/expense-forms/';
            const expensesResponse = await axios.get(expensesUrl, config);
            setExpenses(expensesResponse.data);

            const titlesResponse = await axios.get('/api/expense-titles/', config);
            setExpenseTitles(titlesResponse.data);

            if (titleId && expensesResponse.data.length > 0) {
                const firstExpense = expensesResponse.data[0];
                setTitleStatus({
                    status: firstExpense.status || '',
                    comments: firstExpense.comments || ''
                });
            } else {
                setTitleStatus({
                    status: '',
                    comments: ''
                });
            }

            // Calculate statistics
            calculateStats(expensesResponse.data);
            setLoading(false);
        } catch (error) {
            setError('Failed to fetch data');
            setLoading(false);
        }
    };

    const calculateStats = (expensesData) => {
        const total = expensesData.length;
        const pending = expensesData.filter(e => e.status === 'PENDING').length;
        const approved = expensesData.filter(e => e.status === 'APPROVED').length;
        const rejected = expensesData.filter(e => e.status === 'REJECTED').length;

        setStats({
            totalExpenses: total,
            pendingCount: pending,
            approvedCount: approved,
            rejectedCount: rejected
        });
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('isAdmin');
        localStorage.removeItem('username');
        navigate('/login');
    };

    const handleTitleSelect = (titleId) => {
        setSelectedTitleId(titleId);
        setPendingEdits({});
        fetchData(titleId);
        setActiveTab('details');
    };

    const selectedTitle = expenseTitles.find(title => title.id === selectedTitleId);
    const filteredExpenses = selectedTitleId
        ? expenses.filter(expense => expense.expense_title && expense.expense_title.id === selectedTitleId)
        : expenses;

    const filteredTitles = expenseTitles.filter(title =>
        title.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const handleSearch = (e) => {
        setSearchQuery(e.target.value);
        setShowSuggestions(true);
    };

    const handleSuggestionClick = (titleId) => {
        setSelectedTitleId(titleId);
        setSearchQuery('');
        setShowSuggestions(false);
    };

    const handleSearchBlur = () => {
        setTimeout(() => {
            setShowSuggestions(false);
        }, 200);
    };

    const handleAdminUpdate = async (expenseId, updatedFields) => {
        const csrftoken = getCookie('csrftoken');
        const token = localStorage.getItem('token');
        try {
            const response = await axios.patch(`/api/expense-forms/${expenseId}/update_status/`, updatedFields, {
                headers: {
                    'X-CSRFToken': csrftoken,
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
            });
            setExpenses(prevExpenses =>
                prevExpenses.map(expense =>
                    expense.id === expenseId
                        ? { ...expense, ...updatedFields }
                        : expense
                )
            );
            setPendingEdits(prev => {
                const newEdits = { ...prev };
                delete newEdits[expenseId];
                return newEdits;
            });
            calculateStats(expenses.map(e => e.id === expenseId ? { ...e, ...updatedFields } : e));
        } catch (error) {
            console.error('Error updating expense:', error.response?.data || error.message);
            setError('Failed to update expense. Please try again.');
        }
    };

    const handleTitleStatusUpdate = async () => {
        if (!selectedTitleId || !titleStatus.status) return;

        const csrftoken = getCookie('csrftoken');
        const token = localStorage.getItem('token');

        try {
            const formsToUpdate = expenses.filter(expense => 
                expense.expense_title?.id === selectedTitleId
            );

            setExpenses(prevExpenses => 
                prevExpenses.map(expense => 
                    expense.expense_title?.id === selectedTitleId
                        ? { 
                            ...expense, 
                            status: titleStatus.status,
                            comments: titleStatus.comments
                        }
                        : expense
                )
            );

            const promises = formsToUpdate.map(expense => 
                axios.patch(`/api/expense-forms/${expense.id}/update_status/`, {
                    status: titleStatus.status,
                    comments: titleStatus.comments
                }, {
                    headers: {
                        'X-CSRFToken': csrftoken,
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    },
                })
            );

            await Promise.all(promises);
            calculateStats(expenses.map(e => 
                e.expense_title?.id === selectedTitleId 
                    ? { ...e, status: titleStatus.status, comments: titleStatus.comments } 
                    : e
            ));

            const expensesUrl = `/api/expense-forms/?title_id=${selectedTitleId}`;
            const config = {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            };

            const response = await axios.get(expensesUrl, config);
            setExpenses(response.data);
            calculateStats(response.data);

            setError('');
        } catch (error) {
            console.error('Error updating expenses:', error.response?.data || error.message);
            setError('Failed to update expenses. Please try again.');
            fetchData(selectedTitleId);
        }
    };

    const handleEditExpense = (expense) => {
        setEditingExpense(expense.id);
        setPendingEdits({
            [expense.id]: {
                amount: expense.amount,
                date: expense.date,
                master_group: expense.master_group,
                subgroup: expense.subgroup,
                currency: expense.currency
            }
        });
    };

    const handleMasterGroupChange = (expenseId, value) => {
        setPendingEdits(prev => ({
            ...prev,
            [expenseId]: {
                ...prev[expenseId],
                master_group: value,
                subgroup: ''
            }
        }));
    };

    const handleCancelEdit = () => {
        setEditingExpense(null);
        setPendingEdits({});
    };

    const handleSaveEdit = async (expenseId) => {
        const csrftoken = getCookie('csrftoken');
        const token = localStorage.getItem('token');

        try {
            const response = await axios.patch(`/api/expense-forms/${expenseId}/`, 
                pendingEdits[expenseId],
                {
                    headers: {
                        'X-CSRFToken': csrftoken,
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json'
                    },
                }
            );

            setExpenses(prevExpenses => 
                prevExpenses.map(expense => 
                    expense.id === expenseId 
                        ? { ...expense, ...response.data }
                        : expense
                )
            );

            setEditingExpense(null);
            setPendingEdits({});
            setError('');
            calculateStats(expenses.map(e => e.id === expenseId ? { ...e, ...response.data } : e));
        } catch (error) {
            console.error('Error updating expense:', error.response?.data || error.message);
            setError('Failed to update expense. Please try again.');
        }
    };

    const handleAddTitle = async () => {
        if (!newTitle.trim()) return;

        const csrftoken = getCookie('csrftoken');
        const token = localStorage.getItem('token');

        try {
            const response = await axios.post('/api/expense-titles/', {
                title: newTitle
            }, {
                headers: {
                    'X-CSRFToken': csrftoken,
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            setExpenseTitles(prev => [...prev, response.data]);
            setNewTitle('');
            setError('');
        } catch (error) {
            console.error('Error adding title:', error.response?.data || error.message);
            setError('Failed to add title. Please try again.');
        }
    };

    const handleCopyExpense = async () => {
        if (!newTitle.trim() || !copySourceTitleId) return;

        const csrftoken = getCookie('csrftoken');
        const token = localStorage.getItem('token');

        try {
            // Create new title
            const titleResponse = await axios.post('/api/expense-titles/', {
                title: newTitle
            }, {
                headers: {
                    'X-CSRFToken': csrftoken,
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                }
            });

            const newTitleId = titleResponse.data.id;

            if (copyForms) {
                // Get all forms from source title
                const sourceForms = expenses.filter(expense => 
                    expense.expense_title?.id === copySourceTitleId
                );

                // Create copies of all forms for the new title
                const formPromises = sourceForms.map(form => 
                    axios.post('/api/expense-forms/', {
                        ...form,
                        expense_title: newTitleId,
                        id: undefined // Remove ID to create new record
                    }, {
                        headers: {
                            'X-CSRFToken': csrftoken,
                            'Authorization': `Bearer ${token}`,
                            'Content-Type': 'application/json'
                        }
                    })
                );

                await Promise.all(formPromises);
            }

            // Refresh data
            setExpenseTitles(prev => [...prev, titleResponse.data]);
            setNewTitle('');
            setCopySourceTitleId(null);
            setShowCopyOptions(false);
            setError('');
        } catch (error) {
            console.error('Error copying expense:', error.response?.data || error.message);
            setError('Failed to copy expense. Please try again.');
        }
    };

    const handleDeleteExpense = (expenseId) => {
        setExpenseToDelete(expenseId);
        setShowDeleteConfirm(true);
    };

    const confirmDeleteExpense = async () => {
        if (!expenseToDelete) return;

        const csrftoken = getCookie('csrftoken');
        const token = localStorage.getItem('token');

        try {
            await axios.delete(`/api/expense-forms/${expenseToDelete}/`, {
                headers: {
                    'X-CSRFToken': csrftoken,
                    'Authorization': `Bearer ${token}`
                }
            });

            setExpenses(prev => prev.filter(expense => expense.id !== expenseToDelete));
            setShowDeleteConfirm(false);
            setExpenseToDelete(null);
            calculateStats(expenses.filter(e => e.id !== expenseToDelete));
            setError('');
        } catch (error) {
            console.error('Error deleting expense:', error.response?.data || error.message);
            setError('Failed to delete expense. Please try again.');
            setShowDeleteConfirm(false);
        }
    };

    const handleViewAttachment = (attachmentUrl) => {
        setCurrentAttachment(attachmentUrl);
        setShowAttachmentModal(true);
    };

    const getCategoryData = () => {
        const categoryMap = {};

        filteredExpenses.forEach(expense => {
            const category = expense.master_group || 'Other';
            const amount = parseFloat(expense.amount) || 0;

            if (categoryMap[category]) {
                categoryMap[category] += amount;
            } else {
                categoryMap[category] = amount;
            }
        });

        return Object.entries(categoryMap).map(([name, value]) => ({
            name,
            value
        }));
    };

    const getStatusData = () => {
        const statusCounts = {
            PENDING: 0,
            APPROVED: 0,
            REJECTED: 0
        };

        filteredExpenses.forEach(expense => {
            const status = expense.status || 'PENDING';
            statusCounts[status]++;
        });

        return Object.entries(statusCounts).map(([name, value]) => ({
            name,
            value
        }));
    };

    const username = localStorage.getItem('username');

    if (loading) return <div className="container">Loading...</div>;
    if (error) return <div className="container error-message">{error}</div>;

    return (
        <div className="container">
            <div className="app-title-bar">
                <div className="logo-container">
                    <img src={logoImage} alt="Logo" className="logo-img" />
                    <span className="title">Expense Dashboard</span>
                </div>
                <div className="search-wrapper">
                    <div className="search-container">
                        <input
                            type="text"
                            placeholder="Search expense titles..."
                            value={searchQuery}
                            onChange={handleSearch}
                            onFocus={() => setShowSuggestions(true)}
                            onBlur={handleSearchBlur}
                            className="search-input"
                        />
                        {showSuggestions && searchQuery && (
                            <div className="search-suggestions">
                                {filteredTitles.length > 0 ? (
                                    filteredTitles.map((title) => (
                                        <div
                                            key={title.id}
                                            className="suggestion-item"
                                            onClick={() => handleSuggestionClick(title.id)}
                                        >
                                            {title.title}
                                        </div>
                                    ))
                                ) : (
                                    <div className="no-results">No matching titles found</div>
                                )}
                            </div>
                        )}
                    </div>
                    <button onClick={handleLogout} className="btn">Logout</button>
                </div>
            </div>

            <div className="dashboard-overview">
                <div className="stats-card">
                    <h3>Expense Overview</h3>
                    <div className="stats-grid">
                        <div className="stat-item">
                            <div className="stat-value">{stats.totalExpenses}</div>
                            <div className="stat-label">Total Expenses</div>
                        </div>
                        <div className="stat-item">
                            <div className="stat-value">{stats.pendingCount}</div>
                            <div className="stat-label">Pending</div>
                        </div>
                        <div className="stat-item">
                            <div className="stat-value">{stats.approvedCount}</div>
                            <div className="stat-label">Approved</div>
                        </div>
                        <div className="stat-item">
                            <div className="stat-value">{stats.rejectedCount}</div>
                            <div className="stat-label">Rejected</div>
                        </div>
                    </div>
                </div>

                <div className="quick-actions">
                    <button className="btn" onClick={() => navigate('/home')}>
                        Create New Expense
                    </button>
                    <button className="btn" onClick={() => setActiveTab('reports')}>
                        View Reports
                    </button>
                </div>
            </div>

            <div className="expense-details-container">
                <div className="expense-titles-sidebar">
                    <div className="card">
                        <h3>Expense Titles</h3>
                        <div className="add-title-container">
                            <input
                                type="text"
                                value={newTitle}
                                onChange={(e) => setNewTitle(e.target.value)}
                                placeholder="New title"
                                className="title-input"
                            />
                            <div className="title-action-buttons">
                                <button
                                    className="btn btn-add"
                                    onClick={handleAddTitle}
                                >
                                    Add Title
                                </button>
                                <button
                                    className="btn btn-copy"
                                    onClick={() => setShowCopyOptions(!showCopyOptions)}
                                >
                                    Copy Expense
                                </button>
                            </div>
                            {showCopyOptions && (
                                <div className="copy-options">
                                    <select
                                        value={copySourceTitleId || ''}
                                        onChange={(e) => setCopySourceTitleId(e.target.value)}
                                        className="copy-select"
                                    >
                                        <option value="">Select title to copy</option>
                                        {expenseTitles.map(title => (
                                            <option key={title.id} value={title.id}>{title.title}</option>
                                        ))}
                                    </select>
                                    <div className="copy-checkbox">
                                        <input
                                            type="checkbox"
                                            id="copyForms"
                                            checked={copyForms}
                                            onChange={(e) => setCopyForms(e.target.checked)}
                                        />
                                        <label htmlFor="copyForms">Copy all forms</label>
                                    </div>
                                    <button
                                        className="btn btn-confirm-copy"
                                        onClick={handleCopyExpense}
                                    >
                                        Confirm Copy
                                    </button>
                                </div>
                            )}
                        </div>
                        {expenseTitles.map((title) => (
                            <div
                                key={title.id}
                                className={`expense-item ${selectedTitleId === title.id ? 'selected' : ''}`}
                                onClick={() => handleTitleSelect(title.id)}
                                style={{ cursor: 'pointer' }}
                            >
                                <p>{title.title}</p>
                                <span className={`status-indicator ${expenses.find(e => e.expense_title?.id === title.id)?.status?.toLowerCase() || 'pending'}`}>
                                    {expenses.find(e => e.expense_title?.id === title.id)?.status || 'Pending'}
                                </span>
                            </div>
                        ))}
                    </div>
                </div>

                <div className="expense-forms-main">
                    <div className="tabs">
                        <button
                            className={`tab-btn ${activeTab === 'details' ? 'active' : ''}`}
                            onClick={() => setActiveTab('details')}
                        >
                            Expense Details
                        </button>
                        <button
                            className={`tab-btn ${activeTab === 'reports' ? 'active' : ''}`}
                            onClick={() => setActiveTab('reports')}
                        >
                            Reports & Analytics
                        </button>
                    </div>

                    {activeTab === 'details' && (
                        <div className="expense-forms-container">
                            <div className="card">
                                <h3>Expense Forms</h3>
                                {selectedTitle && <h2>{selectedTitle.title}</h2>}

                                {filteredExpenses.length === 0 ? (
                                    <div className="no-expenses">
                                        No expenses found for this title. Create a new expense form.
                                    </div>
                                ) : (
                                    filteredExpenses.map((expense) => (
                                        <div key={expense.id} className="expense-item">
                                            <div className="form-row">
                                                <div className="form-group">
                                                    <div>Master Group</div>
                                                    {editingExpense === expense.id ? (
                                                        <select
                                                            value={pendingEdits[expense.id]?.master_group || expense.master_group}
                                                            onChange={(e) => handleMasterGroupChange(expense.id, e.target.value)}
                                                        >
                                                            {MASTER_GROUPS.map(([value, label]) => (
                                                                <option key={value} value={value}>{label}</option>
                                                            ))}
                                                        </select>
                                                    ) : (
                                                        <div>{MASTER_GROUPS.find(([value]) => value === expense.master_group)?.[1] || expense.master_group}</div>
                                                    )}
                                                </div>

                                                <div className="form-group">
                                                    <div>Subgroup</div>
                                                    {editingExpense === expense.id ? (
                                                        <select
                                                            value={pendingEdits[expense.id]?.subgroup || expense.subgroup}
                                                            onChange={(e) => setPendingEdits(prev => ({
                                                                ...prev,
                                                                [expense.id]: {
                                                                    ...prev[expense.id],
                                                                    subgroup: e.target.value
                                                                }
                                                            }))}
                                                        >
                                                            {SUBGROUPS[pendingEdits[expense.id]?.master_group || expense.master_group]?.map(([value, label]) => (
                                                                <option key={value} value={value}>{label}</option>
                                                            ))}
                                                        </select>
                                                    ) : (
                                                        <div>{SUBGROUPS[expense.master_group]?.find(([value]) => value === expense.subgroup)?.[1] || expense.subgroup}</div>
                                                    )}
                                                </div>
                                            </div>

                                            <div className="form-row">
                                                <div className="form-group">
                                                    <div>CURR</div>
                                                    {editingExpense === expense.id ? (
                                                        <select
                                                            value={pendingEdits[expense.id]?.currency || expense.currency}
                                                            onChange={(e) => setPendingEdits(prev => ({
                                                                ...prev,
                                                                [expense.id]: {
                                                                    ...prev[expense.id],
                                                                    currency: e.target.value
                                                                }
                                                            }))}
                                                        >
                                                            {CURRENCIES.map(([value, label]) => (
                                                                <option key={value} value={value}>{label}</option>
                                                            ))}
                                                        </select>
                                                    ) : (
                                                        <div>{expense.currency}</div>
                                                    )}
                                                </div>

                                                <div className="form-group">
                                                    <div>Amount</div>
                                                    {editingExpense === expense.id ? (
                                                        <input
                                                            type="number"
                                                            value={pendingEdits[expense.id]?.amount || expense.amount}
                                                            onChange={(e) => setPendingEdits(prev => ({
                                                                ...prev,
                                                                [expense.id]: {
                                                                    ...prev[expense.id],
                                                                    amount: e.target.value
                                                                }
                                                            }))}
                                                        />
                                                    ) : (
                                                        <div>{expense.amount}</div>
                                                    )}
                                                </div>
                                            </div>

                                            <div className="form-row">
                                                <div className="form-group">
                                                    <div>Date</div>
                                                    {editingExpense === expense.id ? (
                                                        <input
                                                            type="date"
                                                            value={pendingEdits[expense.id]?.date || expense.date}
                                                            onChange={(e) => setPendingEdits(prev => ({
                                                                ...prev,
                                                                [expense.id]: {
                                                                    ...prev[expense.id],
                                                                    date: e.target.value
                                                                }
                                                            }))}
                                                        />
                                                    ) : (
                                                        <div>{expense.date}</div>
                                                    )}
                                                </div>
                                            </div>

                                            {expense.attachment && (
                                                <div className="form-group">
                                                    <div>Attachment</div>
                                                    <button
                                                        onClick={() => handleViewAttachment(expense.attachment)}
                                                        className="attachment-link"
                                                    >
                                                        View Attachment
                                                    </button>
                                                </div>
                                            )}

                                            <div className="form-group">
                                                <div>Status</div>
                                                <div className={`status-display ${expense.status?.toLowerCase()}`}>
                                                    {expense.status || 'Pending'}
                                                </div>
                                            </div>
                                            <div className="form-group">
                                                <div>Comments</div>
                                                <div className="comments-display">
                                                    {expense.comments || 'No comments'}
                                                </div>
                                            </div>

                                            <div className="form-actions">
                                                {editingExpense === expense.id ? (
                                                    <>
                                                        <button
                                                            className="btn"
                                                            onClick={() => handleSaveEdit(expense.id)}
                                                        >
                                                            Save Changes
                                                        </button>
                                                        <button
                                                            className="btn btn-secondary"
                                                            onClick={handleCancelEdit}
                                                        >
                                                            Cancel
                                                        </button>
                                                    </>
                                                ) : (
                                                    <>
                                                        <button
                                                            className="btn"
                                                            onClick={() => handleEditExpense(expense)}
                                                        >
                                                            Edit Form
                                                        </button>
                                                        <button
                                                            className="btn btn-danger"
                                                            onClick={() => handleDeleteExpense(expense.id)}
                                                        >
                                                            Delete
                                                        </button>
                                                    </>
                                                )}
                                            </div>
                                        </div>
                                    ))
                                )}
                            </div>
                        </div>
                    )}

                    {activeTab === 'reports' && (
                        <div className="reports-container">
                            <div className="card">
                                <h3>Expense Analytics</h3>

                                <div className="chart-container">
                                    <h4>Expense by Category</h4>
                                    <ResponsiveContainer width="100%" height={300}>
                                        <BarChart
                                            data={getCategoryData()}
                                            margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                                        >
                                            <CartesianGrid strokeDasharray="3 3" />
                                            <XAxis dataKey="name" />
                                            <YAxis />
                                            <Tooltip />
                                            <Legend />
                                            <Bar dataKey="value" fill="#8884d8" name="Amount" />
                                        </BarChart>
                                    </ResponsiveContainer>
                                </div>

                                <div className="chart-container">
                                    <h4>Expense Status Distribution</h4>
                                    <ResponsiveContainer width="100%" height={300}>
                                        <PieChart>
                                            <Pie
                                                data={getStatusData()}
                                                cx="50%"
                                                cy="50%"
                                                labelLine={false}
                                                outerRadius={80}
                                                fill="#8884d8"
                                                dataKey="value"
                                                nameKey="name"
                                                label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
                                            >
                                                {getStatusData().map((entry, index) => (
                                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                                ))}
                                            </Pie>
                                            <Tooltip />
                                            <Legend />
                                        </PieChart>
                                    </ResponsiveContainer>
                                </div>

                                <div className="export-actions">
                                    <button className="btn">
                                        Export as CSV
                                    </button>
                                    <button className="btn">
                                        Export as PDF
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>

            {/* Delete Confirmation Modal */}
            {showDeleteConfirm && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>Confirm Deletion</h3>
                        <p>Are you sure you want to delete this expense? This action cannot be undone.</p>
                        <div className="modal-actions">
                            <button className="btn btn-danger" onClick={confirmDeleteExpense}>
                                Delete
                            </button>
                            <button className="btn btn-secondary" onClick={() => setShowDeleteConfirm(false)}>
                                Cancel
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {/* Attachment Modal */}
            {showAttachmentModal && (
                <div className="modal-overlay">
                    <div className="modal-content attachment-modal">
                        <h3>Attachment Preview</h3>
                        <div className="attachment-preview">
                            {currentAttachment.endsWith('.pdf') ? (
                                <embed 
                                    src={currentAttachment} 
                                    type="application/pdf" 
                                    width="100%" 
                                    height="500px" 
                                />
                            ) : (
                                <img 
                                    src={currentAttachment} 
                                    alt="Expense attachment" 
                                    style={{ maxWidth: '100%', maxHeight: '500px' }} 
                                />
                            )}
                        </div>
                        <div className="modal-actions">
                            <a 
                                href={currentAttachment} 
                                download 
                                className="btn"
                                target="_blank"
                                rel="noopener noreferrer"
                            >
                                Download
                            </a>
                            <button 
                                className="btn btn-secondary" 
                                onClick={() => setShowAttachmentModal(false)}
                            >
                                Close
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default DetailsScreen;
