import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './styles.css';
import logoImage from '../logo.jpg';

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.startsWith(name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

const DetailsScreen = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [expenses, setExpenses] = useState([]);
  const [expenseTitles, setExpenseTitles] = useState([]);
  const [selectedTitleId, setSelectedTitleId] = useState(null);
  const [pendingEdits, setPendingEdits] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    const adminStatus = localStorage.getItem('isAdmin') === 'true';
    setIsAdmin(adminStatus);
    console.log('Is Admin:', adminStatus);
    if (location.state?.selectedTitleId) {
      setSelectedTitleId(location.state.selectedTitleId);
    }
    fetchData();
  }, [location.state?.selectedTitleId]);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      };

      const expensesResponse = await axios.get('/api/expense-forms/', config);
      setExpenses(expensesResponse.data);

      const titlesResponse = await axios.get('/api/expense-titles/', config);
      setExpenseTitles(titlesResponse.data);

      setLoading(false);
    } catch (error) {
      setError('Failed to fetch data');
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    localStorage.removeItem('username');
    navigate('/login');
  };

  const handleTitleSelect = (titleId) => {
    setSelectedTitleId(titleId);
    setPendingEdits({});
  };

  const selectedTitle = expenseTitles.find(title => title.id === selectedTitleId);
  const filteredExpenses = selectedTitleId
    ? expenses.filter(expense => expense.expense_title && expense.expense_title.id === selectedTitleId)
    : expenses;

  const handleAdminUpdate = async (expenseId, updatedFields) => {
    const csrftoken = getCookie('csrftoken');
    const token = localStorage.getItem('token');
    console.log('Attempting to update expense:', expenseId, updatedFields);
    try {
      await axios.patch(`/api/expense-forms/${expenseId}/update_status/`, updatedFields, {
        headers: {
          'X-CSRFToken': csrftoken,
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
      });
      fetchData();
      setPendingEdits(prev => {
        const newEdits = { ...prev };
        delete newEdits[expenseId];
        return newEdits;
      });
    } catch (error) {
      console.error('Error updating expense:', error.response?.data || error.message);
    }
  };

  if (loading) return <div className="container">Loading...</div>;
  if (error) return <div className="container error-message">{error}</div>;

  return (
    <div className="container">
      <div className="app-title-bar">
        <div className="logo-container">
          <img src={logoImage} alt="Logo" className="logo-img" />
          <span className="title">Expense Details</span>
        </div>
        <button onClick={handleLogout} className="btn">Logout</button>
      </div>

      <div className="expense-details-container">
        <div className="expense-titles-sidebar">
          <div className="card">
            <h3>Expense Titles</h3>
            {expenseTitles.map((title) => (
              <div
                key={title.id}
                className={`expense-item ${selectedTitleId === title.id ? 'selected' : ''}`}
                onClick={() => handleTitleSelect(title.id)}
                style={{ cursor: 'pointer' }}
              >
                <p>{title.title}</p>
              </div>
            ))}
          </div>
        </div>

        <div className="expense-forms-main">
          <div className="expense-forms-container">
            <div className="card">
              <h3>Expense Forms</h3>
              {selectedTitle && <h2>{selectedTitle.title}</h2>}
              {filteredExpenses.map((expense) => (
                <div key={expense.id} className="expense-item">
                  <div className="form-row">
                    <div className="form-group">
                      <div>Master Group</div>
                      {isAdmin ? (
                        <div>{expense.master_group}</div>
                      ) : (
                        <input
                          type="text"
                          value={pendingEdits[expense.id]?.master_group || expense.master_group}
                          onChange={(e) => setPendingEdits(prev => ({ ...prev, [expense.id]: { ...prev[expense.id], master_group: e.target.value } }))}
                        />
                      )}
                    </div>

                    <div className="form-group">
                      <div>Subgroup</div>
                      {isAdmin ? (
                        <div>{expense.subgroup}</div>
                      ) : (
                        <input
                          type="text"
                          value={pendingEdits[expense.id]?.subgroup || expense.subgroup}
                          onChange={(e) => setPendingEdits(prev => ({ ...prev, [expense.id]: { ...prev[expense.id], subgroup: e.target.value } }))}
                        />
                      )}
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <div>Amount</div>
                      {isAdmin ? (
                        <div>{expense.amount} {expense.currency}</div>
                      ) : (
                        <input
                          type="number"
                          value={pendingEdits[expense.id]?.amount || expense.amount}
                          onChange={(e) => setPendingEdits(prev => ({ ...prev, [expense.id]: { ...prev[expense.id], amount: e.target.value } }))}
                        />
                      )}
                    </div>

                    <div className="form-group">
                      <div>Date</div>
                      {isAdmin ? (
                        <div>{new Date(expense.date).toLocaleDateString()}</div>
                      ) : (
                        <input
                          type="date"
                          value={pendingEdits[expense.id]?.date || expense.date}
                          onChange={(e) => setPendingEdits(prev => ({ ...prev, [expense.id]: { ...prev[expense.id], date: e.target.value } }))}
                        />
                      )}
                    </div>
                  </div>

                  {isAdmin && expense.attachment && (
                    <div className="form-group">
                      <div>Attachment</div>
                      <a
                        href={expense.attachment}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="attachment-link"
                      >
                        View Attachment
                      </a>
                    </div>
                  )}

                  <div className="form-group">
                    <div>Status</div>
                    {isAdmin ? (
                      <select
                        value={pendingEdits[expense.id]?.status || expense.status.toLowerCase()}
                        onChange={(e) => setPendingEdits(prev => ({ ...prev, [expense.id]: { ...prev[expense.id], status: e.target.value.toUpperCase() } }))}
                      >
                        <option value="pending">Pending</option>
                        <option value="approved">Approved</option>
                        <option value="rejected">Rejected</option>
                      </select>
                    ) : (
                      <div>
                        {expense.status}
                      </div>
                    )}
                  </div>

                  <div className="form-group">
                    <div>Comments</div>
                    {isAdmin ? (
                      <textarea
                        value={pendingEdits[expense.id]?.comments || expense.comments || ''}
                        onChange={(e) => setPendingEdits(prev => ({ ...prev, [expense.id]: { ...prev[expense.id], comments: e.target.value } }))}
                      />
                    ) : (
                      <div>{expense.comments || 'N/A'}</div>
                    )}
                  </div>

                  {isAdmin && pendingEdits[expense.id] && (
                    <button
                      className="btn"
                      onClick={() => handleAdminUpdate(expense.id, pendingEdits[expense.id])}
                    >
                      Save
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DetailsScreen;
