import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import ExpenseForm from './ExpenseForm';
import './styles.css';
import logoImage from '../logo.jpg';

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.startsWith(name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

const HomeScreen = () => {
  const navigate = useNavigate();
  const [expenseTitle, setExpenseTitle] = useState('');
  const [error, setError] = useState('');
  const [expenseTitles, setExpenseTitles] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [activeFormId, setActiveFormId] = useState(null);
  const [showCopyModal, setShowCopyModal] = useState(false);
  const [copySourceTitleId, setCopySourceTitleId] = useState(null);
  const [newTitleName, setNewTitleName] = useState('');
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    const adminStatus = localStorage.getItem('isAdmin') === 'true';
    setIsAdmin(adminStatus);
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const titlesResponse = await axios.get('/api/expense-titles/');
      setExpenseTitles(titlesResponse.data);

      const expensesResponse = await axios.get('/api/expense-forms/');
      setExpenses(expensesResponse.data);

    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!expenseTitle.trim()) {
      setError('Please fill in the expense title');
      return;
    }

    const csrftoken = getCookie('csrftoken');
    const token = localStorage.getItem('token');

    try {
      const titleResponse = await axios.post('/api/expense-titles/', {
        title: expenseTitle
      }, {
        headers: {
          'X-CSRFToken': csrftoken,
          'Authorization': `Bearer ${token}`,
        },
      });

      setExpenseTitles([titleResponse.data, ...expenseTitles]);
      setExpenseTitle('');
      setError('');
    } catch (error) {
      setError('Failed to create expense title. Please try again.');
    }
  };

  const handleAddForm = (titleId) => {
    setActiveFormId(titleId);
  };

  const handleCloseForm = () => {
    setActiveFormId(null);
    fetchData();
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('isAdmin');
    localStorage.removeItem('username');
    navigate('/login');
  };

  const handleCopyTitle = async () => {
    if (!copySourceTitleId || !newTitleName.trim()) {
      setError('Please select a source title and enter a new title name.');
      return;
    }

    const csrftoken = getCookie('csrftoken');
    const token = localStorage.getItem('token');

    try {
      const newTitleResponse = await axios.post('/api/expense-titles/', {
        title: newTitleName
      }, {
        headers: {
          'X-CSRFToken': csrftoken,
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      const newTitleId = newTitleResponse.data.id;

      const sourceForms = expenses.filter(expense => 
        expense.expense_title === parseInt(copySourceTitleId)
      );

      const copyPromises = sourceForms.map(form => 
        axios.post('/api/expense-forms/', {
          ...form,
          expense_title: newTitleId,
          id: undefined,
          status: 'PENDING',
          comments: ''
        }, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'X-CSRFToken': csrftoken,
            'Content-Type': 'application/json'
          }
        })
      );

      await Promise.all(copyPromises);

      await fetchData();
      setShowCopyModal(false);
      setNewTitleName('');
      setCopySourceTitleId(null);
      setError('');

    } catch (error) {
      console.error('Error copying title:', error);
      setError('Failed to copy expense title. Please try again.');
    }
  };

  return (
    <div className="container">
      <div className="app-title-bar">
        <div className="logo-container">
          <img src={logoImage} alt="Logo" className="logo-img" />
          <span className="title">Expense Management</span>
        </div>
        <button onClick={handleLogout} className="btn">Logout</button>
      </div>

      <div className="card">
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="expenseTitle">New Expense Title</label>
            <input
              type="text"
              id="expenseTitle"
              value={expenseTitle}
              onChange={(e) => setExpenseTitle(e.target.value)}
              required
              placeholder="Enter new expense title"
            />
          </div>
          <div className="form-group">
            <button type="submit" className="btn">
              Add Title
            </button>
          </div>
        </form>
      </div>

      <div>
        {expenseTitles.map((title) => (
          <div key={title.id} className="card expense-title-card">
            <h3 className="expense-title-heading"
              onClick={() => navigate('/details', { state: { selectedTitleId: title.id } })}
              style={{ cursor: 'pointer' }}
            >
              {title.title}
            </h3>
            <div className="button-group">
              <button
                onClick={() => {
                  setShowCopyModal(true);
                  setCopySourceTitleId(title.id);
                }}
                className="btn copy-btn"
              >
                 Copy Title
              </button>
              <button
                onClick={() => handleAddForm(title.id)}
                className="btn add-form-button"
              >
                âž• Add Form
              </button>
            </div>
            {activeFormId === title.id && (
              <div className="expense-form-container">
                <ExpenseForm
                  titleId={title.id}
                  isAdmin={isAdmin}
                  onClose={handleCloseForm}
                />
              </div>
            )}
          </div>
        ))}
      </div>

      {showCopyModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>Copy Expense Title</h3>
             {error && <div className="error-message">{error}</div>}
            <div className="form-group">
              <label>Source Title</label>
              <select
                value={copySourceTitleId || ''}
                onChange={(e) => setCopySourceTitleId(e.target.value)}
                className="modal-select"
                disabled
              >
                 <option value="">{expenseTitles.find(t => t.id === copySourceTitleId)?.title || 'Select a title to copy'}</option>
              </select>
            </div>
            <div className="form-group">
              <label>New Title Name</label>
              <input
                type="text"
                value={newTitleName}
                onChange={(e) => setNewTitleName(e.target.value)}
                placeholder="Enter new title name"
                className="modal-input"
              />
            </div>
            <div className="modal-actions">
              <button
                className="btn"
                onClick={handleCopyTitle}
                disabled={!copySourceTitleId || !newTitleName.trim()}
              >
                Copy
              </button>
              <button
                className="btn btn-secondary"
                onClick={() => {
                  setShowCopyModal(false);
                  setNewTitleName('');
                  setCopySourceTitleId(null);
                  setError('');
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomeScreen;
