import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css';

// Configure axios defaults
axios.defaults.baseURL = 'http://localhost:8000';  // Update this to match your Django server port
axios.defaults.withCredentials = true;

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.startsWith(name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

const MASTER_GROUPS = [
  ['TRAVEL', 'Travel'],
  ['OFFICE_SUPPLIES', 'Office Supplies'],
  ['UTILITIES', 'Utilities'],
];

const CURRENCIES = [
  ['USD', 'USD'],
  ['EUR', 'EUR'],
  ['GBP', 'GBP'],
  ['INR', 'INR'],
  ['CAD', 'CAD'],
];

const SUBGROUPS = {
  'TRAVEL': [
    ['TICKET', 'Ticket Expense'],
    ['FOOD', 'Food Expense'],
    ['HOSPITALITY', 'Hospitality Expense'],
  ],
  'OFFICE_SUPPLIES': [
    ['EQUIPMENT', 'Equipment'],
    ['STATIONERY', 'Stationery'],
  ],
  'UTILITIES': [
    ['INTERNET', 'Internet'],
    ['ELECTRICITY', 'Electricity'],
  ],
};

const ExpenseForm = ({ titleId, isAdmin, onClose, existingAttachments = [], expenses }) => {
  const [formData, setFormData] = useState({
    master_group: 'TRAVEL',
    subgroup: 'TICKET',
    currency: 'USD',
    amount: '',
    date: new Date().toISOString().split('T')[0],
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);
  const [amountError, setAmountError] = useState('');
  const [attachments, setAttachments] = useState(existingAttachments);

  const validateAmount = (value) => {
    if (value === '') {
      setAmountError('Amount is required');
      return false;
    }
    if (isNaN(value)) {
      setAmountError('Please enter a valid number');
      return false;
    }
    if (Number(value) <= 0) {
      setAmountError('Amount must be greater than 0');
      return false;
    }
    const decimalPlaces = value.toString().split('.')[1]?.length || 0;
    if (decimalPlaces > 2) {
      setAmountError('Only two decimal places are allowed');
      return false;
    }
    setAmountError('');
    return true;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setAttachments([...attachments, ...files]);
  };

  const handleRemoveAttachment = (index) => {
    const newAttachments = [...attachments];
    newAttachments.splice(index, 1);
    setAttachments(newAttachments);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setLoading(true);
    if (!validateAmount(formData.amount)) {
      setLoading(false);
      return;
    }
    if (!titleId) {
      setError('Expense title is required.');
      setLoading(false);
      return;
    }
    try {
      const csrftoken = getCookie('csrftoken');
      const token = localStorage.getItem('token');
      const formDataToSend = new FormData();

      // Add all other fields except attachments
      Object.keys(formData).forEach(key => {
        if (key !== 'attachments' && formData[key] !== null) {
          formDataToSend.append(key, formData[key]);
        }
      });

      // âœ… Append all files under the same key
      attachments.forEach((file) => {
        formDataToSend.append('attachments', file);
      });

      // Always include expense_title_id for backend
      formDataToSend.append('expense_title_id', titleId);
      formDataToSend.append('status', 'PENDING');
      formDataToSend.append('comments', '');

      // Submit the new form
      const response = await axios.post('/api/expense-forms/', formDataToSend, {
        headers: {
          'X-CSRFToken': csrftoken,
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      // Update all forms under this title to PENDING status
      const updatePromises = expenses
        .filter(expense => expense.expense_title?.id === titleId)
        .map(expense => 
          axios.patch(
            `/api/expense-forms/${expense.id}/update_status/`,
            {
              status: 'PENDING',
              comments: ''
            },
            {
              headers: {
                'X-CSRFToken': csrftoken,
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
              }
            }
          )
        );

      await Promise.all(updatePromises);

      setSuccess(true);
      setFormData({
        master_group: 'TRAVEL',
        subgroup: 'TICKET',
        currency: 'USD',
        amount: '',
        date: new Date().toISOString().split('T')[0],
      });
      setAttachments([]);
      // Close the form after successful submission
      onClose();
    } catch (error) {
      setError(error.response?.data?.message || error.response?.data?.detail || 'Failed to submit expense. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Helper to get file name from URL
  const getAttachmentName = (url) => {
    try {
      const urlObj = new URL(url);
      const pathname = urlObj.pathname;
      const filename = pathname.split('/').pop();
      return decodeURIComponent(filename);
    } catch (error) {
      return 'Unknown file';
    }
  };

  // Subgroup options update on master_group change
  useEffect(() => {
    setFormData(prev => ({
      ...prev,
      subgroup: SUBGROUPS[formData.master_group]?.[0]?.[0] || ''
    }));
    // eslint-disable-next-line
  }, [formData.master_group]);

  return (
    <div className="expense-form-container">
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="master_group">MASTER GROUP</label>
            <select
              id="master_group"
              name="master_group"
              value={formData.master_group}
              onChange={handleChange}
              required
            >
              {MASTER_GROUPS.map(([value, label]) => (
                <option key={value} value={value}>{label}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="subgroup">SUBGROUP</label>
            <select
              id="subgroup"
              name="subgroup"
              value={formData.subgroup}
              onChange={handleChange}
              required
            >
              {SUBGROUPS[formData.master_group]?.map(([value, label]) => (
                <option key={value} value={value}>{label}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="currency">CURR</label>
            <select
              id="currency"
              name="currency"
              value={formData.currency}
              onChange={handleChange}
              required
            >
              {CURRENCIES.map(([value, label]) => (
                <option key={value} value={value}>{label}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label htmlFor="amount">AMOUNT</label>
            <input
              type="text"
              id="amount"
              name="amount"
              value={formData.amount}
              onChange={handleChange}
              required
              placeholder="0.00"
            />
            {amountError && <div className="amount-error-callout">{amountError}</div>}
          </div>
          <div className="form-group">
            <label htmlFor="date">DATE</label>
            <input
              type="date"
              id="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
            />
          </div>
        </div>

        <div className="attachments-section">
          <div style={{ textAlign: 'center', fontWeight: 'bold', margin: '1rem 0' }}>Attachments</div>
          {/* Existing attachments list (if any) */}
          {attachments && attachments.length > 0 && attachments.map((url, idx) => (
            <div key={idx} className="current-attachment">
              <a href={url} target="_blank" rel="noopener noreferrer" style={{ color: '#00b4d8' }}>{getAttachmentName(url)}</a>
              <button type="button" className="view-btn" onClick={() => window.open(url, '_blank', 'noopener,noreferrer')}>View</button>
            </div>
          ))}
          {/* New attachments list */}
          {attachments.map((file, index) => (
            <div key={index} className="current-attachment">
              <span>{file.name}</span>
              <button
                type="button"
                className="btn-delete"
                onClick={() => handleRemoveAttachment(index)}
              >
                Delete
              </button>
            </div>
          ))}
          {/* File picker for multiple uploads */}
          <div className="new-attachment">
            <input
              type="file"
              id="attachments"
              name="attachments"
              onChange={handleFileChange}
              className="file-input"
              multiple
            />
          </div>
        </div>
        {/* Save button for add form mode */}
        <div className="expense-form-actions">
          <button type="submit" className="btn btn-save" disabled={loading}>
            {loading ? 'Saving...' : 'Save'}
          </button>
          <button type="button" className="btn btn-cancel" onClick={onClose} disabled={loading}>
            Cancel
          </button>
        </div>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">Expense submitted successfully!</div>}
      </form>
    </div>
  );
};

export default ExpenseForm;
