import React, { useState } from 'react';
import axios from 'axios';
import './styles.css';

// Configure axios defaults
axios.defaults.baseURL = 'http://localhost:8000';  // Update this to match your Django server port
axios.defaults.withCredentials = true;

function getCookie(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.startsWith(name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
}

const ExpenseForm = ({ titleId, isAdmin, onClose }) => {
    const [formData, setFormData] = useState({
        expense_title_id: titleId,
        master_group: '',
        subgroup: '',
        amount: '',
        currency: 'USD',
        date: new Date().toISOString().split('T')[0],
        attachment1: null,
        attachment2: null
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value, files } = e.target;

        if (name === 'attachment1' || name === 'attachment2') {
            setFormData(prev => ({
                ...prev,
                [name]: files[0]
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        const csrftoken = getCookie('csrftoken');
        const token = localStorage.getItem('token');

        try {
            const formDataToSend = new FormData();

            // Add all form fields
            Object.keys(formData).forEach(key => {
                if (key === 'attachment1' || key === 'attachment2') {
                    if (formData[key]) {
                        formDataToSend.append(key, formData[key]);
                    }
                } else {
                    formDataToSend.append(key, formData[key]);
                }
            });

            const response = await axios.post('/api/expense-forms/', formDataToSend, {
                headers: {
                    'X-CSRFToken': csrftoken,
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data'
                },
                withCredentials: true
            });

            onClose();
        } catch (error) {
            console.error('Error submitting form:', error.response?.data || error.message);
            setError(error.response?.data?.detail || 'Failed to submit form. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="expense-form-container">
            <form onSubmit={handleSubmit} className="card">
                <h3>Add New Expense Form</h3>
                {error && <div className="error-message">{error}</div>}

                <div className="form-group">
                    <label>Master Group</label>
                    <select
                        name="master_group"
                        value={formData.master_group}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Master Group</option>
                        <option value="TRAVEL">Travel</option>
                        <option value="OFFICE_SUPPLIES">Office Supplies</option>
                        <option value="UTILITIES">Utilities</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Subgroup</label>
                    <select
                        name="subgroup"
                        value={formData.subgroup}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Select Subgroup</option>
                        {formData.master_group === 'TRAVEL' && (
                            <>
                                <option value="TICKET">Ticket Expense</option>
                                <option value="FOOD">Food Expense</option>
                                <option value="HOSPITALITY">Hospitality Expense</option>
                            </>
                        )}
                        {formData.master_group === 'OFFICE_SUPPLIES' && (
                            <>
                                <option value="EQUIPMENT">Equipment</option>
                                <option value="STATIONERY">Stationery</option>
                            </>
                        )}
                        {formData.master_group === 'UTILITIES' && (
                            <>
                                <option value="INTERNET">Internet</option>
                                <option value="ELECTRICITY">Electricity</option>
                            </>
                        )}
                    </select>
                </div>

                <div className="form-group">
                    <label>Amount</label>
                    <input
                        type="number"
                        name="amount"
                        value={formData.amount}
                        onChange={handleChange}
                        step="0.01"
                        min="0"
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Currency</label>
                    <select
                        name="currency"
                        value={formData.currency}
                        onChange={handleChange}
                        required
                    >
                        <option value="USD">USD</option>
                        <option value="EUR">EUR</option>
                        <option value="GBP">GBP</option>
                        <option value="INR">INR</option>
                        <option value="CAD">CAD</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Date</label>
                    <input
                        type="date"
                        name="date"
                        value={formData.date}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Attachment 1</label>
                    <input
                        type="file"
                        name="attachment1"
                        onChange={handleChange}
                        className="file-input"
                    />
                </div>

                <div className="form-group">
                    <label>Attachment 2</label>
                    <input
                        type="file"
                        name="attachment2"
                        onChange={handleChange}
                        className="file-input"
                    />
                </div>

                <div className="form-actions">
                    <button type="submit" className="btn" disabled={loading}>
                        {loading ? 'Submitting...' : 'Submit'}
                    </button>
                    <button type="button" className="btn btn-secondary" onClick={onClose}>
                        Cancel
                    </button>
                </div>
            </form>
        </div>
    );
};

export default ExpenseForm;
